import { GET_LOGS, SET_LOADING, LOGS_ERROR } from '../actions/types';

// javaScript object
const initialState = {
    logs: null,
    current: null,
    loading: false,
    error: null
}

export default (state = initialState, action) => {
    switch (action.type) {
        case GET_LOGS: {
            return {
                ...state,
                logs: action.payload,
                loading: true
            }
        }
        case SET_LOADING: {
            return {
                ...state,
                loading: true,
            }
        }
        case LOGS_ERROR: {
            console.error(action.payload);
            return {
                ...state,
                error: action.payload
            }
        }
        default: return state
    }
}
