import { SET_LOADING, GET_TECHS, ADD_TECH, DELETE_TECH, TECHS_ERROR } from '../actions/types';

/* 
CLEAR_LOGS,
GET_TECHS,
ADD_TECH,
DELETE_TECH,
TECHS_ERROR,
 */

// javaScript object
const initialState = {
    techs: null,
    loading: false,
    error: null
}

export default (state = initialState, action) => {
    switch (action.type) {
        case CLEAR_CURRENT: {
            return {
                ...state,
                current: null,
            }
        }
        case SET_LOADING: {
            return {
                ...state,
                loading: true,
            }
        }
        case LOGS_ERROR: {
            console.error(action.payload);
            return {
                ...state,
                error: action.payload
            }
        }
        default: return state
    }
}
