import { SET_LOADING, GET_TECHS, ADD_TECH, DELETE_TECH, TECHS_ERROR } from '../actions/types';

/* 
CLEAR_LOGS,
GET_TECHS,
ADD_TECH,
DELETE_TECH,
TECHS_ERROR,
 */

// javaScript object
const initialState = {
    techs: null,
    loading: false,
    error: null
}

export default (state = initialState, action) => {
    switch (action.type) {
        case GET_TECHS: {
            return {
                ...state,
                techs: action.payload,
                loading: false
            }
        }
        case SET_LOADING: {
            return {
                ...state,
                loading: true
            }
        }
        case ADD_TECH: {
            return {
                ...state,
                techs: [...state.techs, action.payload],
                loading: false
            }
        }
        case DELETE_TECH: {
            return {
                ...state,
                techs: state.techs.filter(tech => tech.id !== action.payload),
                loading: false
            }
        }
        case TECHS_ERROR: {
            console.error(action.payload);
            return {
                error: action.payload,
                loading: false
            }
        }

        default: return state
    }
}
