import React, { useState, useEffect } from 'react';
import TechItem from './TechItem';

/** 
 *!  Component এর সাথে Redux এর connect করার জন্য 
 **  { connect } redux এর একটি built in function
**/
import { connect } from 'react-redux';

/** 
 *! Data validation এর জন্য
 **/
import PropTypes from 'prop-types';


const TechListModel = () => {

    const [techs, setTechs] = useState([])
    const [loading, setLoading] = useState(false)

    useEffect(() => {
        // eslint-disable-next-line
        getTechs();
    }, [])


    const getTechs = async () => {
        setLoading(true);
        const res = await fetch('/techs')
        const data = await res.json()

        /** 
         *! db.json file er all data hear --> (data)
        **/
        setTechs(data)
        setLoading(false)
    }


    return (
        <div id="tech-list-modal" className="modal">
            <div className="modal-content">
                <h4> Technician List</h4>
                <ul className="collection">
                    {!loading && techs.map(tech => (
                        <TechItem tech={tech} key={tech.id} />
                    ))}
                </ul>
            </div>
        </div>
    );
};


/** 
 **  Data validation check এর জন্য
 **/
TechListModel.propTypes = {

}

export default connect()(TechListModel);
